var lawyer = [{
  title: "עורך דין חוזים תל אביב",
  answer: "\u05D0\u05E0\u05D5 \u05DE\u05EA\u05DE\u05D7\u05D9\u05DD \u05D1\u05E2\u05E8\u05D9\u05DB\u05EA \u05D7\u05D5\u05D6\u05D9\u05DD \u05D5\u05DB\u05EA\u05D9\u05D1\u05EA \u05D4\u05E1\u05DB\u05DE\u05D9\u05DD. \u05DE\u05E9\u05E8\u05D3\u05E0\u05D5 \u05DE\u05E6\u05D9\u05E2 \u05D0\u05EA \u05E9\u05D9\u05E8\u05D5\u05EA\u05D9\u05D5 \u05D1\u05DB\u05DC \u05D4\u05D0\u05E8\u05E5 \u05DC\u05E8\u05D1\u05D5\u05EA \u05D1\u05D0\u05D6\u05D5\u05E8\u05D9 \u05EA\u05DC \u05D0\u05D1\u05D9\u05D1 \u05D5\u05D9\u05E8\u05D5\u05E9\u05DC\u05D9\u05DD. \u05D1\u05D0\u05EA\u05E8 \u05E0\u05D9\u05EA\u05DF \u05DC\u05E8\u05DB\u05D5\u05E9 \u05D4\u05E1\u05DB\u05DE\u05D9\u05DD \u05DE\u05E7\u05D9\u05E4\u05D9\u05DD \u05D1\u05EA\u05D7\u05D5\u05DE\u05D9 \u05DE\u05E9\u05E4\u05D8 \u05E9\u05D5\u05E0\u05D9\u05DD \u05D5\u05D2\u05DD \u05DC\u05E7\u05D1\u05DC \u05DE\u05E2\u05E0\u05D4 \u05D0\u05D5\u05DF \u05DC\u05D9\u05D9\u05DF \u05DE\u05E2\u05D5\u05E8\u05DA \u05D3\u05D9\u05DF \u05D7\u05D5\u05D6\u05D9\u05DD \u05DE\u05E7\u05E6\u05D5\u05E2\u05D9 \u05D1\u05D0\u05D5\u05E4\u05DF \u05D9\u05E2\u05D9\u05DC \u05D5\u05DE\u05D4\u05D9\u05E8. \u05E6\u05E8\u05D5 \u05E7\u05E9\u05E8 \u05DB\u05E2\u05EA."
}, {
  title: "עורך דין חוזים",
  answer: "\u05DE\u05E6\u05D0\u05EA\u05DD \u05D7\u05D5\u05D6\u05D4 \u05D1\u05D0\u05D9\u05E0\u05D8\u05E8\u05E0\u05D8 \u05D5\u05D0\u05EA\u05DD \u05D7\u05D5\u05E9\u05D1\u05D9\u05DD \u05DC\u05D4\u05E9\u05EA\u05DE\u05E9 \u05D1\u05D5? \u05EA\u05D7\u05E9\u05D1\u05D5 \u05E9\u05D5\u05D1. \u05E8\u05D5\u05D1 \u05D4\u05D7\u05D5\u05D6\u05D9\u05DD \u05D0\u05D5 \u05D4\u05E1\u05DB\u05DE\u05D9\u05DD \u05E9\u05D0\u05E0\u05D5 \u05DE\u05D5\u05E6\u05D0\u05D9\u05DD \u05D1\u05E8\u05D7\u05D1\u05D9 \u05D4\u05D0\u05D9\u05E0\u05D8\u05E8\u05E0\u05D8 \u05DC\u05D0 \u05DE\u05D2\u05D9\u05E0\u05D9\u05DD \u05E2\u05DC \u05D6\u05DB\u05D5\u05D9\u05D5\u05EA\u05D9\u05DB\u05DD \u05D5\u05D6\u05D0\u05EA \u05D1\u05DC\u05E9\u05D5\u05DF \u05D4\u05DE\u05E2\u05D8\u05D4. \u05D7\u05E9\u05D9\u05D1\u05D5\u05EA\u05D5 \u05E9\u05DC \u05E2\u05D5\u05E8\u05DA \u05D3\u05D9\u05DF \u05D7\u05D5\u05D6\u05D9\u05DD \u05D4\u05D9\u05D0 \u05DC\u05D5\u05D5\u05D3\u05D0 \u05DB\u05D9 \u05D0\u05EA\u05DD \u05D5\u05D6\u05DB\u05D5\u05D9\u05D5\u05EA\u05D9\u05DB\u05DD \u05DE\u05D5\u05D2\u05E0\u05D9\u05DD \u05D1\u05D0\u05D5\u05E4\u05DF \u05DE\u05E7\u05E1\u05D9\u05DE\u05DC\u05D9 \u05DB\u05DA \u05E9\u05D1\u05DE\u05E7\u05E8\u05D4 \u05D5\u05D1\u05D5 \u05D4\u05E6\u05D3 \u05E9\u05DB\u05E0\u05D2\u05D3 \u05D9\u05E4\u05E8 \u05D0\u05EA \u05D4\u05D7\u05D5\u05D6\u05D4 \u05E9\u05D7\u05EA\u05DD \u05DE\u05D5\u05DC\u05DB\u05DD \u05DC\u05D0 \u05EA\u05E2\u05DE\u05D3\u05D5 \u05D1\u05E4\u05E0\u05D9 \u05E9\u05D5\u05E7\u05EA \u05E9\u05D1\u05D5\u05E8\u05D4. \u05E6\u05E8\u05D5 \u05E7\u05E9\u05E8 \u05DB\u05E2\u05EA \u05E2\u05DD \u05E2\u05D5\u05E8\u05DA \u05D3\u05D9\u05DF \u05D7\u05D5\u05D6\u05D9\u05DD \u05DE\u05E7\u05E6\u05D5\u05E2\u05D9 \u05D5\u05D1\u05E2\u05DC \u05E0\u05D9\u05E1\u05D9\u05D5\u05DF \u05DE\u05DE\u05E9\u05E8\u05D3\u05E0\u05D5."
}, {
  title: "טיפ מנצח עבור כתיבת הסכמים",
  answer: "\u05E2\u05DC \u05E2\u05D5\u05E8\u05DA \u05D3\u05D9\u05DF \u05D7\u05D5\u05D6\u05D9\u05DD \u05DC\u05E6\u05E4\u05D5\u05EA \u05E4\u05E0\u05D9 \u05E2\u05EA\u05D9\u05D3 \u05D5\u05DC\u05D3\u05D0\u05D5\u05D2 \u05DB\u05D9 \u05D1\u05E2\u05EA \u05DB\u05EA\u05D9\u05D1\u05EA \u05D4\u05E1\u05DB\u05DE\u05D9\u05DD \u05D4\u05D5\u05D0 \u05DE\u05EA\u05D9\u05D9\u05D7\u05E1 \u05DC\u05DE\u05E7\u05E8\u05D9\u05DD \u05D4\u05E2\u05EA\u05D9\u05D3\u05D9\u05DD \u05DC\u05D4\u05EA\u05DE\u05DE\u05E9 \u05DB\u05E4\u05D5\u05E2\u05DC \u05D9\u05D5\u05E6\u05D0 \u05DE\u05DB\u05E8\u05D9\u05EA\u05EA \u05D4\u05D4\u05E1\u05DB\u05DD \u05D1\u05D9\u05DF \u05D4\u05E6\u05D3\u05D3\u05D9\u05DD. \u05D1\u05DB\u05EA\u05D9\u05D1\u05EA \u05D4\u05E1\u05DB\u05DE\u05D9\u05DD \u05D9\u05E9 \u05DC\u05D4\u05D1\u05D9\u05D0 \u05D1\u05D7\u05E9\u05D1\u05D5\u05DF \"\u05DE\u05E7\u05E8\u05D9\u05DD \u05D5\u05EA\u05D2\u05D5\u05D1\u05D5\u05EA\" \u05D5\u05E4\u05D9\u05E6\u05D5\u05D9\u05D9\u05DD \u05DE\u05D5\u05E1\u05DB\u05DE\u05D9\u05DD \u05D1\u05DE\u05D9\u05D3\u05D4 \u05D5\u05E6\u05D3 \u05DC\u05D0 \u05DE\u05E7\u05D9\u05D9\u05DD \u05D0\u05EA \u05D4\u05D5\u05E8\u05D0\u05D5\u05EA \u05D4\u05D4\u05E1\u05DB\u05DD. "
}, {
  title: "ניסוח הסכם שותפות",
  answer: "\u05E0\u05D9\u05E1\u05D5\u05D7 \u05D4\u05E1\u05DB\u05DD \u05E9\u05D5\u05EA\u05E4\u05D5\u05EA \u05D8\u05D5\u05D1 \u05D4\u05D5\u05D0 \u05DB\u05D6\u05D4 \u05D4\u05DE\u05E1\u05D3\u05D9\u05E8 \u05D0\u05EA \u05DE\u05E2\u05E8\u05DB\u05EA \u05D4\u05D9\u05D7\u05E1\u05D9\u05DD \u05D5\u05D4\u05EA\u05D7\u05D9\u05D9\u05D1\u05D5\u05EA \u05E9\u05DC \u05D4\u05E9\u05D5\u05EA\u05E4\u05D9\u05DD \u05D1\u05E9\u05D5\u05EA\u05E4\u05D5\u05EA \u05D1\u05D9\u05E0\u05DD \u05DC\u05D1\u05D9\u05DF \u05E2\u05E6\u05DE\u05DD. \u05DC\u05D3\u05D5\u05D2\u05DE\u05D0 \u05E2\u05DC\u05D9\u05D5 \u05DC\u05D4\u05D2\u05D3\u05D9\u05E8: \n  \u05D0. \u05DE\u05D4 \u05EA\u05E4\u05E7\u05D9\u05D3\u05D5 \u05E9\u05DC \u05DB\u05DC \u05E9\u05D5\u05EA\u05E3 \u05D5\u05DE\u05D4 \u05D4\u05DD \u05D4\u05EA\u05D7\u05D9\u05D9\u05D1\u05D5\u05D9\u05D5\u05EA\u05D9\u05D5.\n  \u05D1. \u05DE\u05E0\u05D2\u05E0\u05D5\u05E0\u05D9 \u05E4\u05E2\u05D5\u05DC\u05D4 \u05D1\u05DE\u05E7\u05E8\u05D4 \u05E9\u05DC \u05D4\u05E4\u05E8\u05EA \u05D4\u05EA\u05D7\u05D9\u05D9\u05D1\u05D5\u05EA \u05E9\u05DC \u05DE\u05D9 \u05DE\u05D4\u05E9\u05D5\u05EA\u05E4\u05D9\u05DD.\n  \u05D2. \u05EA\u05D4\u05DC\u05D9\u05DB\u05D9 \u05D1\u05D5\u05E8\u05E8\u05D5\u05EA \u05D5\u05D2\u05D9\u05E9\u05D5\u05E8 \u05DE\u05D5\u05E1\u05DB\u05DE\u05D9\u05DD.\n  \u05D3. \u05E9\u05DE\u05D9\u05E8\u05EA \u05E1\u05D5\u05D3\u05D9\u05D5\u05EA \u05D5\u05E2\u05D5\u05D3. \n  \u05D0\u05D9\u05DF \u05E0\u05D9\u05E1\u05D5\u05D7 \u05D4\u05E1\u05DB\u05DD \u05E9\u05D5\u05EA\u05E4\u05D5\u05EA \u05D0\u05D7\u05D3 \u05D3\u05D5\u05DE\u05D4 \u05DC\u05E9\u05E0\u05D9 \u05D5\u05D6\u05D0\u05EA \u05E2\u05E7\u05D1 \u05D4\u05E0\u05E1\u05D9\u05D1\u05D5\u05EA \u05D5\u05D4\u05E6\u05E8\u05DB\u05D9\u05DD \u05D4\u05E9\u05D5\u05E0\u05D9\u05DD \u05E9\u05DC \u05DB\u05DC \u05E9\u05D5\u05EA\u05E4\u05D5\u05EA. \u05E6\u05E8\u05D5 \u05E7\u05E9\u05E8 \u05E2\u05DD \u05DE\u05E9\u05E8\u05D3\u05E0\u05D5 \u05DB\u05E2\u05EA \u05D5\u05E0\u05E0\u05E1\u05D7 \u05E2\u05D1\u05D5\u05E8\u05DB\u05DD \u05D4\u05E1\u05DB\u05DD \u05E9\u05D5\u05EA\u05E4\u05D5\u05EA.\" \n  "
}];
