  h3Content: "\u05D6\u05D5\u05D2\u05D5\u05EA \u05D4\u05DE\u05E2\u05D5\u05E0\u05D9\u05D9\u05E0\u05D9\u05DD \u05DC\u05DE\u05E1\u05D3 \u05D0\u05EA \u05D4\u05E7\u05E9\u05E8 \u05D1\u05D9\u05E0\u05D9\u05D4\u05DD \u05DB\u05D9\u05D3\u05D5\u05E2\u05D9\u05DD \u05D1\u05E6\u05D9\u05D1\u05D5\u05E8 \u05E0\u05E2\u05D6\u05E8\u05D9\u05DD \u05DC\u05D0 \u05E4\u05E2\u05DD \u05D1\u05E9\u05D9\u05E8\u05D5\u05EA\u05D9\u05D5 \u05E9\u05DC \u05E2\u05D5\u05E8\u05DA \u05D3\u05D9\u05DF \u05DC\u05E2\u05E0\u05D9\u05D9\u05E0\u05D9 \u05D9\u05D3\u05D5\u05E2\u05D9\u05DD \u05D1\u05E6\u05D9\u05D1\u05D5\u05E8. \u05DC\u05E6\u05E2\u05E8\u05E0\u05D5, \u05D6\u05D5\u05D2\u05D5\u05EA \u05D9\u05D3\u05D5\u05E2\u05D9\u05DD \u05D1\u05E6\u05D9\u05D1\u05D5\u05E8 \u05E2\u05D3\u05D9\u05D9\u05DF \u05E0\u05EA\u05E7\u05DC\u05D9\u05DD \u05D1\u05E7\u05E9\u05D9\u05D9 \u05D1\u05D9\u05E8\u05D5\u05E7\u05E8\u05D8\u05D9\u05D4 \u05D1\u05DE\u05D5\u05E1\u05D3\u05D5\u05EA \u05E9\u05DC\u05D8\u05D5\u05E0\u05D9\u05D9\u05DD \u05D1\u05D4\u05DD \u05D6\u05D5\u05D2\u05D5\u05EA \u05E0\u05D3\u05E8\u05E9\u05D9\u05DD \u05DC\u05D4\u05D5\u05DB\u05D9\u05D7 \u05D0\u05EA \u05E7\u05D9\u05D5\u05DD \u05D7\u05D9\u05D9\u05D4\u05DD \u05D4\u05DE\u05E9\u05D5\u05EA\u05E4\u05D9\u05DD \u05D0\u05D5 \u05D4\u05D9\u05D5\u05EA\u05DD \u05D6\u05D5\u05D2 \u05D9\u05D3\u05D5\u05E2 \u05D1\u05E6\u05D9\u05D1\u05D5\u05E8. \u05D4\u05DC\u05D9\u05DA \u05D6\u05D9\u05D4\u05D5\u05D9 \u05D4\u05D6\u05D5\u05D2 \u05DB\u05D9\u05D3\u05D5\u05E2\u05D9\u05DD \u05D1\u05E6\u05D9\u05D1\u05D5\u05E8 \u05DE\u05EA\u05D1\u05E6\u05E2 \u05E2\u05DC \u05D9\u05D3\u05D9 \u05EA\u05E6\u05D4\u05D9\u05E8 \u05D9\u05D3\u05D5\u05E2\u05D9\u05DD \u05D1\u05E6\u05D9\u05D1\u05D5\u05E8 \u05D7\u05EA\u05D5\u05DD \u05E2\u05DC \u05D9\u05D3\u05D9 \u05E2\u05D5\u05E8\u05DA \u05D3\u05D9\u05DF. \u05D7\u05E9\u05D5\u05D1 \u05DC\u05D4\u05D3\u05D2\u05D9\u05E9 \u05DB\u05D9 \u05DE\u05DC\u05D1\u05D3 \u05D4\u05D7\u05D9\u05E1\u05E8\u05D5\u05DF \u05D4\u05D1\u05D9\u05E8\u05D5\u05E7\u05E8\u05D8\u05D9 \u05D4\u05DE\u05E4\u05D5\u05E8\u05D8 \u05DE\u05E2\u05DC\u05D4, \u05D9\u05D3\u05D5\u05E2\u05D9\u05DD \u05D1\u05E6\u05D9\u05D1\u05D5\u05E8 \u05D6\u05DB\u05D0\u05D9\u05DD \u05D1\u05DE\u05D3\u05D9\u05E0\u05EA \u05D9\u05E9\u05E8\u05D0\u05DC \u05DC\u05DB\u05DE\u05E2\u05D8 \u05D0\u05D5\u05EA\u05DD \u05D6\u05DB\u05D5\u05D9\u05D5\u05EA \u05DB\u05E4\u05D9 \u05E9\u05D6\u05D5\u05D2 \u05E0\u05E9\u05D5\u05D9.       ",
  h4: 'הסכם חיים משותפים ידועים בציבור',
