  seoHeader: "\u05E6\u05D5\u05D5\u05D0\u05D4 - \u05DE\u05D9\u05D3\u05E2 \u05DE\u05E7\u05D9\u05E3 \u05D5\u05D9\u05D9\u05E2\u05D5\u05E5 \u05E8\u05D0\u05E9\u05D5\u05E0\u05D9 / \u05E9\u05D9\u05E8\u05D5\u05EA \u05E8\u05DB\u05D9\u05E9\u05EA \u05E6\u05D5\u05D5\u05D0\u05D4 \u05D0\u05D5\u05DF \u05DC\u05D9\u05D9\u05DF /  \u05E2\u05D5\u05E8\u05DA \u05D3\u05D9\u05DF \u05E6\u05D5\u05D5\u05D0\u05D4 \u05D0\u05DC\u05E2\u05D3 \u05DB\u05D4\u05DF",
  seoDescription: "\u05E2\u05E8\u05D9\u05DB\u05EA \u05E6\u05D5\u05D5\u05D0\u05D4 - \u05E8\u05DB\u05D9\u05E9\u05EA \u05E6\u05D5\u05D5\u05D0\u05D4 \u05DE\u05E7\u05D9\u05E4\u05D4 \u05D1\u05DE\u05D4\u05D9\u05E8\u05D5\u05EA \u05D5\u05D1\u05E4\u05E9\u05D8\u05D5\u05EA \u05D0\u05D5\u05DF \u05DC\u05D9\u05D9\u05DF - \u05DB\u05EA\u05D9\u05D1\u05EA \u05E6\u05D5\u05D5\u05D0\u05D4 -  \u05E2\u05D5\u05E8\u05DA \u05D3\u05D9\u05DF \u05E6\u05D5\u05D5\u05D0\u05D4",
  categoryHeb: 'משפחה',
